package com.example.ecohand.presentation.senas

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.automirrored.filled.ArrowBack
import androidx.compose.material.icons.automirrored.filled.ArrowForward
import androidx.compose.material.icons.filled.Person
import androidx.compose.material.icons.filled.Star
import androidx.compose.material.icons.filled.Info
import androidx.compose.material.icons.filled.Build
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.vector.ImageVector
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun CategorySelectionScreen(
    onNavigateBack: () -> Unit,
    onCategorySelected: (String) -> Unit
) {
    val categories = listOf(
        CategoryInfo(
            name = "Alfabeto",
            description = "Letras y vocales en LSP",
            icon = Icons.Default.Star,
            available = true,
            itemCount = 6 // A, E, I, O, U, Z
        ),
        CategoryInfo(
            name = "Relaciones Familiares",
            description = "Señas sobre la familia",
            icon = Icons.Default.Person,
            available = false,
            itemCount = 0
        ),
        CategoryInfo(
            name = "Adverbios",
            description = "Señas de adverbios",
            icon = Icons.Default.Info,
            available = false,
            itemCount = 0
        ),
        CategoryInfo(
            name = "Verbos",
            description = "Acciones y verbos",
            icon = Icons.Default.Build,
            available = false,
            itemCount = 0
        )
    )

    Scaffold(
        topBar = {
            TopAppBar(
                title = {
                    Text(
                        text = "Categorías de Señas",
                        fontWeight = FontWeight.Bold,
                        fontSize = 18.sp
                    )
                },
                navigationIcon = {
                    IconButton(onClick = onNavigateBack) {
                        Icon(
                            imageVector = Icons.AutoMirrored.Filled.ArrowBack,
                            contentDescription = "Volver"
                        )
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = MaterialTheme.colorScheme.primary,
                    titleContentColor = MaterialTheme.colorScheme.onPrimary,
                    navigationIconContentColor = MaterialTheme.colorScheme.onPrimary
                )
            )
        }
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
                .background(MaterialTheme.colorScheme.background)
                .padding(16.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            // Título e instrucciones
            Card(
                modifier = Modifier.fillMaxWidth(),
                colors = CardDefaults.cardColors(
                    containerColor = MaterialTheme.colorScheme.primaryContainer
                ),
                elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
            ) {
                Column(
                    modifier = Modifier.padding(20.dp),
                    horizontalAlignment = Alignment.CenterHorizontally
                ) {
                    Text(
                        text = "️ Validación de Señas",
                        fontSize = 24.sp,
                        fontWeight = FontWeight.Bold,
                        color = MaterialTheme.colorScheme.onPrimaryContainer
                    )
                    Spacer(modifier = Modifier.height(8.dp))
                    Text(
                        text = "Selecciona una categoría para practicar",
                        fontSize = 16.sp,
                        color = MaterialTheme.colorScheme.onPrimaryContainer.copy(alpha = 0.8f),
                        textAlign = TextAlign.Center
                    )
                }
            }

            // Lista de categorías
            LazyColumn(
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                items(categories) { categoryInfo ->
                    CategoryCard(
                        categoryInfo = categoryInfo,
                        onCategoryClick = {
                            if (categoryInfo.available) {
                                onCategorySelected(categoryInfo.name)
                            }
                        }
                    )
                }
            }
        }
    }
}

@Composable
fun CategoryCard(
    categoryInfo: CategoryInfo,
    onCategoryClick: () -> Unit
) {
    ElevatedCard(
        modifier = Modifier.fillMaxWidth(),
        onClick = onCategoryClick,
        colors = CardDefaults.elevatedCardColors(
            containerColor = if (categoryInfo.available) {
                MaterialTheme.colorScheme.surface
            } else {
                MaterialTheme.colorScheme.surfaceVariant.copy(alpha = 0.5f)
            }
        ),
        elevation = CardDefaults.elevatedCardElevation(
            defaultElevation = if (categoryInfo.available) 6.dp else 2.dp
        ),
        enabled = categoryInfo.available
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(20.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Icono de la categoría
            Box(
                modifier = Modifier
                    .size(60.dp)
                    .background(
                        if (categoryInfo.available) {
                            MaterialTheme.colorScheme.primaryContainer
                        } else {
                            MaterialTheme.colorScheme.surfaceVariant
                        },
                        RoundedCornerShape(30.dp)
                    ),
                contentAlignment = Alignment.Center
            ) {
                Icon(
                    imageVector = categoryInfo.icon,
                    contentDescription = categoryInfo.name,
                    modifier = Modifier.size(32.dp),
                    tint = if (categoryInfo.available) {
                        MaterialTheme.colorScheme.onPrimaryContainer
                    } else {
                        MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.5f)
                    }
                )
            }

            Spacer(modifier = Modifier.width(16.dp))

            // Información de la categoría
            Column(
                modifier = Modifier.weight(1f)
            ) {
                Row(
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Text(
                        text = categoryInfo.name,
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold,
                        color = if (categoryInfo.available) {
                            MaterialTheme.colorScheme.onSurface
                        } else {
                            MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.5f)
                        }
                    )

                    if (!categoryInfo.available) {
                        Spacer(modifier = Modifier.width(8.dp))
                        Surface(
                            color = MaterialTheme.colorScheme.secondary.copy(alpha = 0.2f),
                            shape = RoundedCornerShape(12.dp)
                        ) {
                            Text(
                                text = "Próximamente",
                                fontSize = 10.sp,
                                fontWeight = FontWeight.Medium,
                                color = MaterialTheme.colorScheme.secondary,
                                modifier = Modifier.padding(horizontal = 8.dp, vertical = 4.dp)
                            )
                        }
                    }
                }

                Spacer(modifier = Modifier.height(4.dp))

                Text(
                    text = categoryInfo.description,
                    fontSize = 14.sp,
                    color = if (categoryInfo.available) {
                        MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
                    } else {
                        MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.5f)
                    }
                )

                if (categoryInfo.available && categoryInfo.itemCount > 0) {
                    Spacer(modifier = Modifier.height(4.dp))
                    Text(
                        text = "${categoryInfo.itemCount} señas disponibles",
                        fontSize = 12.sp,
                        color = MaterialTheme.colorScheme.primary,
                        fontWeight = FontWeight.Medium
                    )
                }
            }

            // Flecha indicadora
            if (categoryInfo.available) {
                Icon(
                    imageVector = Icons.AutoMirrored.Filled.ArrowForward,
                    contentDescription = "Ir a ${categoryInfo.name}",
                    tint = MaterialTheme.colorScheme.primary
                )
            }
        }
    }
}

data class CategoryInfo(
    val name: String,
    val description: String,
    val icon: ImageVector,
    val available: Boolean,
    val itemCount: Int
)

